command.script:
export output_dir=$(echo @option.output_dir@)
export db=$(echo @option.db@)
export log_count=$(echo @option.log_count@)
export log_size=$(echo @option.log_size@)
export log_level=$(echo @option.log_level@)
export log_dir=$(echo @option.log_dir@)
export log_timestamp=$(echo @option.log_timestamp@)
export username=$(echo @option.username@)
export password=$(echo @option.password@)
export reboot=$(echo @option.reboot@)
export hostSlot=$(echo "@option.listSN@")
export hd=$(echo "@option.list_hd@")
export hostname=$(echo "@option.list_host@")
export shelloutput=$(echo @option.shell_output@)

/opt/.pyenv/shims/python -u /opt/SVTECH-Junos-Automation/Python-Development/atp_hardware_tool/VNPT.v4/phase2_2.py -o "$output_dir" -db "$db" -u "$username" -p "$password" -hd "$hd" -hn "$hostname" -hs "$hostSlot" -reboot "$reboot" --log_size "$log_size" --log_count "$log_count" --log_timestamp "$log_timestamp" --log_level "$log_level" --log_dir "$log_dir" --shell_output "$shelloutput"
